import {
    addProduct,
    deleteProduct,
    editProduct,
    findByIdProduct,
    getCate1,
    getProducts
} from "../../services/productsService";


const initialState = {
    productCate: [],
    products: [],
    product:{},
}
const productsSlice = createSlice({
        name: 'products',
        initialState,
        reducers: {},
        extraReducers: builder => {
            builder.addCase(getProducts.fulfilled, (state, action) => {
                state.products = action.payload
