import {createSlice} from "@reduxjs/toolkit";
import {

    getCate1,

} from "../../services/productCate";

const initialState = {
    productCate: [],

}
const productCate1= createSlice({
        name: 'productCate1',
        initialState,
        reducers: {},
        extraReducers: builder => {


            builder.addCase(getCate1.fulfilled, (state, action) => {
                state.productsCate = action.payload
            });


        }
    }
)

export default productCate1.reducer;
