import {Link, useNavigate} from "react-router-dom";
import {useDispatch, useSelector} from "react-redux";
import {useEffect} from "react";
import {login} from "../services/userService";
import {Field, Form, Formik} from "formik";
import swal from "sweetalert";


export default function Login() {
    const dispatch = useDispatch();
    const navigate = useNavigate();
    const user = useSelector(state=>{
        return state.user.currentUser
    })


    const handleLogin = async (values) =>{
        await dispatch(login(values)).then((e)=>{
            if(e.payload !== 'Username is not existed' && e.payload !== 'Password is wrong'){
                swal(`Well come, "${e.payload.username}"`, {
                    icon: "success",
                })
                navigate('/home')
            }else{
                navigate('/')
            }


        })
    }

    useEffect(() => {
        localStorage.clear()
    }, [])

    return(
        <>

            <div id="login-button">
                <img src="https://dqcgrsy5v35b9.cloudfront.net/cruiseplanner/assets/img/icons/login-w-icon.png">
                </img>
            </div>
            <div id="container">
                <h1>Log In</h1>
                 <span className="close-btn">
                     <img src="https://cdn4.iconfinder.com/data/icons/miu/22/circle_close_delete_-128.png"></img>
                  </span>

                                <form>
                                    <input type="email" name="email" placeholder="E-mail"/>
                                        <input type="password" name="pass" placeholder="Password"/>
                                            <a href="#">Log in</a>
                                            <div id="remember-container">
                                                <input type="checkbox" id="checkbox-2-1" className="checkbox" checked="checked"/>
                                                <span id="remember">Remember me</span>
                                                <span id="forgotten">Forgotten password</span>
                                            </div>
                                </form>
                            </div>

                            <div id="forgotten-container">
                                <h1>Forgotten</h1>
                  <span className="close-btn">
                    <img src="https://cdn4.iconfinder.com/data/icons/miu/22/circle_close_delete_-128.png"></img>
                  </span>

                <form>
                    <input type="email" name="email" placeholder="E-mail"/>
                        <a href="#" className="orange-btn">Get new password</a>
                </form>
            </div>



            {/*<div className="row">*/}
            {/*    <div className="offset-3 col-6 mt-5">*/}
