import {Link, useNavigate} from "react-router-dom";
import {useDispatch, useSelector} from "react-redux";
import {useEffect} from "react";
import {login} from "../services/userService";
import {Field, Form, Formik} from "formik";
import swal from "sweetalert";
import './login.css'


export default function Login() {
    const dispatch = useDispatch();
    const navigate = useNavigate();
    const user = useSelector(state=>{
        return state.user.currentUser
    })


    const handleLogin = async (values) =>{
        await dispatch(login(values)).then((e)=>{
            if(e.payload !== 'Username is not existed' && e.payload !== 'Password is wrong'){
                swal(`Well come, "${e.payload.username}"`, {
                    icon: "success",
                })
                navigate('/home')
            }else{
                navigate('/')
            }


        })
    }

    useEffect(() => {
        localStorage.clear()
    }, [])

    return(
        <>

            <section>

                <div className="box">

                    <div className="square" ></div>
                    <div className="square" ></div>
                    <div className="square" ></div>
                    <div className="square" ></div>
                    <div className="square" ></div>
                    <div className="square" ></div>

                    <div className="container">
                        <div className="form">
                            <h2>LOGIN to CodePen</h2>
                            <form action="">
                                <div className="inputBx">
                                    <input type="text" required="required"/>
                                        <span>Login</span>
                                        <i className="fas fa-user-circle"></i>
                                </div>
                                <div className="inputBx password">
                                    <input id="password-input" type="password" name="password" required="required"/>
                                        <span>Password</span>
                                        <i className="fas fa-key"></i>
                                </div>
                                <label className="remember"><input type="checkbox"/>
                                    Remember</label>
                                <div className="inputBx">
                                    <input type="submit" value="Log in" disabled/>
                                </div>
                            </form>
                            <p>Don't have an account <a href="#">Sign up</a></p>
                        </div>
                    </div>

                </div>
            </section>



            {/*<div className="row">*/}
